    fun data(context: Context) : String {
        return try {
            val inputStream = (context as AppCompatActivity).assets.open("data.json")
            val size = inputStream.available()
            val buffer = ByteArray(size)
            inputStream.read(buffer)
            inputStream.close()
            String(buffer, Charsets.UTF_8)
        } catch (e: IOException){
            e.printStackTrace()
            json
        }
    }

    fun getListMovies() : ArrayList<MovieEntity> {
        val movie = Gson().fromJson(tmdbMovies, MovieDataModel::class.java)
        val movieList = ArrayList<MovieEntity>()
        movie.results.forEach { m ->
            val mv = MovieEntity(m.id, m.overview, m.popularity, m.posterPath, m.releaseDate, m.title, m.voteAverage, m.voteCount)
            movieList.add(mv)
        }
        return movieList
    }

    fun getListTv(): ArrayList<TvEntity> {
        val tv = Gson().fromJson(tmdbTv, TvShowDataModel::class.java)
        val tvList = ArrayList<TvEntity>()
        tv.results.forEach {
            val t = TvEntity(it.id, it.firstAirDate, it.name, it.overview, it.popularity, it.posterPath, it.voteAverage, it.voteCount)
            tvList.add(t)
        }
        return tvList
    }

    fun getDetailMovie(): MovieWithCredit {
        val movie = Gson().fromJson(tmdbDetailMovie, MovieEntity::class.java)
        return MovieWithCredit(movie, getCreditMovie())
    }

    fun getDetailTv(): TvWithCredit {
        val tv = Gson().fromJson(tmdbDetailTv, TvEntity::class.java)
        return TvWithCredit(tv, getCreditTv())
    }

    fun getCreditMovie(): List<CreditsEntity> {
        val movie = Gson().fromJson(tmdbCreditMovie, CreditsDataModel::class.java)
        val movieList = ArrayList<CreditsEntity>()
        movie.cast.forEach {
            val m = CreditsEntity(
                it.id, it.id, it.name, it.profilePath, it.character
            )
            movieList.add(m)
        }
        return movieList
    }

    fun getCreditTv(): List<CreditsTvEntity> {
        val tv = Gson().fromJson(tmdbCreditTv, CreditsDataModel::class.java)
        val tvList = ArrayList<CreditsTvEntity>()
        tv.cast.forEach {
            val t = CreditsTvEntity(
                it.id, it.id, it.name, it.profilePath, it.character
            )
            tvList.add(t)
        }
        return tvList
    }

