    fun data(context: Context) : String {
        return try {
            val inputStream = (context as AppCompatActivity).assets.open("data.json")
            val size = inputStream.available()
            val buffer = ByteArray(size)
            inputStream.read(buffer)
            inputStream.close()
            String(buffer, Charsets.UTF_8)
        } catch (e: IOException){
            e.printStackTrace()
            json
        }
    }

    fun getDummyData() : DataModel {
        return Gson().fromJson(json, DataModel::class.java)
    }

    fun getPopularMovies() : List<MovieEntity> {
        val movie = Gson().fromJson(tmdbMovies, MovieDataModel::class.java)
        val movieList = ArrayList<MovieEntity>()
        movie.results.forEach { m ->
            val mv = MovieEntity(m.adult, m.backdropPath, m.genreIds, m.id, m.originalLanguage, m.originalTitle, m.overview, m.popularity, m.posterPath, m.releaseDate, m.title, m.video, m.voteAverage, m.voteCount)
            movieList.add(mv)
        }
        return movieList
    }

